import React, { useState } from 'react';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    rememberMe: false
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error when user starts typing
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';

      const response = await fetch(`${apiUrl}/api/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Origin': window.location.origin,
          'X-Requested-With': 'XMLHttpRequest',
          'X-Api-Client': 'web-portal'
        },
        body: JSON.stringify({
          username: formData.username,
          password: formData.password
        })
      });

      const data = await response.json();

      if (response.ok && data.token) {
        // Store JWT token
        localStorage.setItem('vubank_token', data.token);
        localStorage.setItem('vubank_user', JSON.stringify(data.user));

        // Call parent component's onLogin
        onLogin({
          ...data.user,
          token: data.token
        });
      } else {
        setError(data.message || 'Login failed. Please check your credentials.');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('Unable to connect to server. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="language-selector">English (UK) â–¼</div>
      <div className="contact-info">Contact us</div>

      <div className="welcome-section">
        <h1 className="welcome-title">Welcome to VuBank</h1>
      </div>

      <div className="login-card">
        <div className="logo">
          <div className="logo-icon">VU</div>
          <div className="logo-text">vubank</div>
        </div>

        <div className="security-icon">ðŸ”’</div>

        <h2 className="login-title">Log in to your account</h2>

        {error && (
          <div className="error-message" style={{ 
            color: '#d32f2f', 
            backgroundColor: '#ffebee', 
            padding: '10px', 
            borderRadius: '4px', 
            marginBottom: '20px', 
            border: '1px solid #ffcdd2' 
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              placeholder="Enter username"
              value={formData.username}
              onChange={handleInputChange}
              required
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter password"
              value={formData.password}
              onChange={handleInputChange}
              required
              autoComplete="current-password"
            />
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleInputChange}
              />
              {' '}Remember me
            </label>
            <button 
              type="button" 
              className="forgot-password"
              onClick={() => alert('Please contact VuBank support for password recovery')}
            >
              Need help logging in?
            </button>
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Logging in...' : 'Continue'}
          </button>
        </form>
      </div>

      <div className="footer-links">
        <div>IPv6 Supported</div>
        <div>VuBank Â© 2025</div>
      </div>
    </div>
  );
};

export default LoginPage;
