import React, { useState } from 'react';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    userIdOrEmail: '',
    groupId: '',
    rememberMe: false
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // For demo purposes, we'll simulate login
      // In a real app, you'd call your backend API
      setTimeout(() => {
        const userData = {
          id: 1,
          name: 'John Doe',
          email: formData.userIdOrEmail,
          groupId: formData.groupId || 'DEFAULT'
        };
        onLogin(userData);
        setIsLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="language-selector">English (UK) â–¼</div>
      <div className="contact-info">Contact us</div>
      <div className="more-services">â‰¡ More services</div>

      <div className="login-card">
        <div className="logo">
          <div className="logo-icon">sc</div>
          <div className="logo-text">standard chartered</div>
        </div>

        <h1 className="welcome-title">Welcome to Straight2Bank</h1>

        <div className="security-icon">ðŸ”’</div>

        <h2 className="login-title">Log in to your account</h2>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="userIdOrEmail">User ID or email</label>
            <input
              type="text"
              id="userIdOrEmail"
              name="userIdOrEmail"
              placeholder="Enter user ID or email"
              value={formData.userIdOrEmail}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="groupId">Group ID</label>
            <input
              type="text"
              id="groupId"
              name="groupId"
              placeholder="Enter group ID"
              value={formData.groupId}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleInputChange}
              />
              Remember me
            </label>
            <a href="#" className="forgot-password">Need help logging in?</a>
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Logging in...' : 'Continue'}
          </button>

          <button type="button" className="token-activation">
            ðŸ”‘ Activate physical/digital token
          </button>
        </form>

        <div className="quickserve">
          <div className="quickserve-title">QuickServe</div>
          <div className="quickserve-buttons">
            <a href="#" className="quickserve-button">
              <div className="quickserve-icon">ðŸ“ˆ</div>
              <div>Trade Track-it</div>
            </a>
            <a href="#" className="quickserve-button">
              <div className="quickserve-icon">ðŸ“‹</div>
              <div>Support Centre</div>
            </a>
          </div>
        </div>
      </div>

      <div className="footer-links">
        <a href="#">Legal Notice</a>
        <a href="#">Service Guides</a>
        <a href="#">Security Centre</a>
        <a href="#">System requirements</a>
        <div>IPv4 Supported Standard Chartered Bank Â© 2025</div>
      </div>
    </div>
  );
};

export default LoginPage;
